import{_ as e,c as r,o as t,d as o}from"./app.d9cc5fcb.js";const g=JSON.parse('{"title":"Welcome to Omnivore","description":"","frontmatter":{},"headers":[{"level":2,"title":"Join us on Discord! ðŸ’¬","slug":"join-us-on-discord","link":"#join-us-on-discord","children":[]},{"level":2,"title":"Shoutouts ðŸŽ‰","slug":"shoutouts","link":"#shoutouts","children":[]}],"relativePath":"index.md"}'),a={name:"index.md"},i=o('<h1 id="welcome-to-omnivore" tabindex="-1">Welcome to Omnivore <a class="header-anchor" href="#welcome-to-omnivore" aria-hidden="true">#</a></h1><p><a href="https://omnivore.app" target="_blank" rel="noreferrer">Omnivore</a> is a complete, open source read-it-later solution for people who like text.</p><p>We built Omnivore because we love reading and we want it to be more fun. Join us!</p><ul><li>Highlighting, notes, search, and sharing</li><li>Full keyboard navigation</li><li>Automatically saves your place in long articles</li><li>Add newsletter articles via email (with substack support!)</li><li>PDF support</li><li><a href="https://omnivore.app/" target="_blank" rel="noreferrer">Web app</a> written in Node.js and TypeScript</li><li><a href="https://omnivore.app/install/ios" target="_blank" rel="noreferrer">Native iOS app</a></li><li>Progressive web app for Android users</li><li>Browser extensions for <a href="https://omnivore.app/install/chrome" target="_blank" rel="noreferrer">Chrome</a>, <a href="https://omnivore.app/install/safari" target="_blank" rel="noreferrer">Safari</a>, <a href="https://omnivore.app/install/firefox" target="_blank" rel="noreferrer">Firefox</a>, and <a href="https://omnivore.app/install/edge" target="_blank" rel="noreferrer">Edge</a></li><li>Labels (aka tagging)</li><li>Offline support</li><li>Text to speech (iOS only)</li><li><a href="https://logseq.com/" target="_blank" rel="noreferrer">Logseq</a> support via our <a href="https://github.com/omnivore-app/logseq-omnivore" target="_blank" rel="noreferrer">Logseq Plugin</a></li></ul><p>Every single part is fully open source! Fork it, extend it, or deploy it to your own server.</p><p>We also have a free hosted version of Omnivore at <a href="https://omnivore.app/" target="_blank" rel="noreferrer">omnivore.app</a> -- try it now!</p><img width="981" alt="omnivore-readme-screenshot" src="https://user-images.githubusercontent.com/75189/153696698-9e4f1bdd-5954-465b-8ab0-b4eacc60f779.png"><h2 id="join-us-on-discord" tabindex="-1">Join us on Discord! ðŸ’¬ <a class="header-anchor" href="#join-us-on-discord" aria-hidden="true">#</a></h2><p>We&#39;re building our community on Discord. <a href="https://discord.gg/h2z5rppzz9" target="_blank" rel="noreferrer">Join us!</a></p><p>Read more about Omnivore on our blog. <a href="https://blog.omnivore.app/p/getting-started-with-omnivore" target="_blank" rel="noreferrer">https://blog.omnivore.app/p/getting-started-with-omnivore</a></p><h2 id="shoutouts" tabindex="-1">Shoutouts ðŸŽ‰ <a class="header-anchor" href="#shoutouts" aria-hidden="true">#</a></h2><p>Omnivore takes advantage of some great open source software:</p><ul><li><a href="https://www.typescriptlang.org/" target="_blank" rel="noreferrer">TypeScript</a> - Most of our backend and frontend are written in TypeScript.</li><li><a href="https://nextjs.org/" target="_blank" rel="noreferrer">Next.js</a> - Our frontend is a Next.JS app and is hosted on <a href="https://vercel.com/" target="_blank" rel="noreferrer">Vercel</a>.</li><li><a href="https://swr.vercel.app/" target="_blank" rel="noreferrer">SWR</a> - We do all our data fetching on the web using SWR.</li><li><a href="https://stitches.dev/" target="_blank" rel="noreferrer">Stitches</a> - We use Stitches on the frontend to style our components.</li><li><a href="https://github.com/mozilla/readability" target="_blank" rel="noreferrer">Mozilla Readability</a> - We use Mozilla&#39;s Readability library to make pages easier to read.</li><li><a href="https://www.swift-graphql.com/" target="_blank" rel="noreferrer">Swift GraphQL</a> - We generate our GraphQL queries on iOS using Swift GraphQL.</li><li><a href="https://www.radix-ui.com/" target="_blank" rel="noreferrer">Radix</a> - We use Radix UI&#39;s components on our frontend.</li><li>And many more awesome libraries, just checkout our package files to see what we are using.</li></ul>',13),n=[i];function l(s,p,h,d,c,u){return t(),r("div",null,n)}const m=e(a,[["render",l]]);export{g as __pageData,m as default};
